import React from "react";
import Input from "./Input";
import Submit from "./Submit";

const Form = (props) => {
  const addIngredients = (input) => {
    return input;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addIngredients();
    props.onSave(input);
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <p>I have these ingredients:</p>
        <Input onSave={addIngredients} />
        <Submit />
      </form>
    </>
  );
};

export default Form;
