import React, { useEffect, useState } from "react";
import RecipeModal from "./RecipeModal";
import LoadingSpinner from "./LoadingSpinner";

const Results = (props) => {
  const [instruction, setInstruction] = useState([]);
  const [loading, setLoading] = useState(false);
  const [show, setShow] = useState(false);
  const apiKey = "f30518619f5b4aaa92944bafe8f2b949";

  useEffect((id) => {
    setLoading(true);
    const generateRecipe = async (id) => {
      const instructionSrc = `https://api.spoonacular.com/recipes/${id}/analyzedInstructions?apiKey=${apiKey}`;
      const res = await fetch(instructionSrc);
      const instructionData = await res.json();
      console.log(instructionData);
      const recipeSteps = instructionData.steps.map((d) => <li>{d.step}</li>);
      setInstruction(recipeSteps);
      setShow(true);
    };
  }, []);

  if (loading) {
    return <LoadingSpinner />;
  }
  const recipes = props.data.map((d) => {
    return (
      <div id={d.id} key={d.id}>
        <img src={d.image} alt="" />
        <br />
        <p>{d.title}</p>
        <button
          type="submit"
          value="submit"
          onClick={() => {
            generateRecipe(d.id);
          }}
        >
          Click for Recipe
        </button>
        <RecipeModal
          title="Instructions"
          message={instruction}
          show={show}
          onClose={() => setShow(false)}
        />
      </div>
    );
  });

  return <div>{recipes}</div>;
};

export default Results;
