import React, { useEffect, useState } from "react";
import RecipeModal from "./RecipeModal";
import LoadingSpinner from "./LoadingSpinner";

const Results = (props) => {
  const [instruction, setInstruction] = useState([]);
  const [wait, setWait] = useState([]);
  const [show, setShow] = useState(false);
  const [dataLoading, setDataLoading] = useState(false);
  const apiKey = "f30518619f5b4aaa92944bafe8f2b949";


  const generateRecipe = async (id) => {
    const url = `https://api.spoonacular.com/recipes/${id}/analyzedInstructions?apiKey=${apiKey}`;
    try {
      const res = await fetch(url);
      const json = await res.json();
      setWait(json);
      console.log(json);
      const recipeSteps = wait.steps.map((d) => <li>{d.step}</li>);
      setInstruction(recipeSteps);
    } catch (error) {
      console.log("error", error);
    }
  };

  // useEffect(() => {
  //   setShow(true);
  //   setDataLoading(true);
  //   generateRecipe();
  //   setDataLoading(false);
  // }, []);
  console.log(props.data)
  const recipes = props.data.map((d) => {
    return (
      <div id={d.id} key={d.id}>
        <img src={d.image} alt="" />
        <br />
        <p>{d.title}</p>
        <button type="submit" value="submit" onClick={generateRecipe(d.id)}>
          Click for Recipe
        </button>
        <RecipeModal
          title="Instructions"
          message={dataLoading ? <LoadingSpinner /> : instruction}
          show={show}
          onClose={() => setShow(false)}
        />
      </div>
    );
  });

  return <div>{recipes}</div>;
};

export default Results;
